mpg |&gt; 
  group_by(class) |&gt; 
  summarize(mean_cty = mean(cty), mean_hwy = mean(hwy)) |&gt; 
  ggplot() +
    geom_point(mapping = aes(x = mean_cty, y = mean_hwy))
